  .replace(/^```json\s*/i, '')
  .replace(/\s*```$/i, '')
  .trim();

// 2. Parse JSON
const obj = JSON.parse(cleaned);

// 3. Convert to array of "company: url"
const result = Object.entries(obj).map(([company, url]) => ({
  company,
  url
}));

return result;

